//_ExpressSection.js
import React from "react";
import {
  ButtonBack,
  ButtonFirst,
  ButtonLast,
  ButtonNext,
  CarouselProvider,
  DotGroup,
  ImageWithZoom,
  Slide,
  Slider,
} from "pure-react-carousel";
import styles from "pure-react-carousel/dist/react-carousel.es.css";
import { makeStyles } from "@material-ui/core/styles";
import john from "../../../assets/img/examples/clem-onojeghuo.jpg";
import tanya from "../../../assets/img/examples/cynthia-del-rio.jpg";

const expStyles = makeStyles(styles);

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      padding: "70px 0",
    },
  }));

export default function AutoGrid() {
  const classes = useStyles();
  const s = expStyles();

  return (
    <div className={classes.root}>
      <CarouselProvider
        visibleSlides={3}
        totalSlides={6}
        step={3}
        naturalSlideWidth={400}
        naturalSlideHeight={500}
        hasMasterSpinner
      >
        <h1 className={s.headline}>Carousel (Custom Spinner)</h1>
        <p>This uses a customized spinner.</p>
        <Slider className={s.slider} 
        //spinner={() => <CustomSpinner />}
        >
          <Slide index={0}>
            <ImageWithZoom
              src={tanya}
              //spinner={() => <CustomSpinner />}
            />
          </Slide>
          <Slide index={1}>
            <ImageWithZoom src={john} />
          </Slide>
          <Slide index={2}>
            <ImageWithZoom src={tanya} />
          </Slide>
          <Slide index={3}>
            <ImageWithZoom src={john} />
          </Slide>
          <Slide index={4}>
            <ImageWithZoom src={tanya} />
          </Slide>
          <Slide index={5}>
            <ImageWithZoom src={john} />
          </Slide>
        </Slider>
        <ButtonFirst>First</ButtonFirst>
        <ButtonBack>Back</ButtonBack>
        <ButtonNext>Next</ButtonNext>
        <ButtonLast>Last</ButtonLast>
        <DotGroup />
      </CarouselProvider>
    </div>
  );
}
